buildGradleAdditions.insert:
def ueVersion = System.getenv("UE_VERSION")

if (ueVersion != null) 
{
    if(
        ueVersion == "4.27" ||
        ueVersion == "5.0"  ||
        ueVersion == "5.1" )
    {
        android.compileSdkVersion 31
        android.defaultConfig {
            minSdkVersion 26
        }
        android.compileOptions {
         sourceCompatibility JavaVersion.VERSION_1_8
         targetCompatibility JavaVersion.VERSION_1_8
        }
    }
    else
    {
        android.compileSdkVersion 34
    }
} 
else 
{
    android.compileSdkVersion 34
}

gameActivityImportAdditions.insert:
import android.content.Intent;
import java.nio.file.Path;
import java.lang.String;

gameActivityClassAdditions.insert:
public void clearBugsnagCache() {
    File cache = getCacheDir();
    deleteRecursive(cache, "bugsnag");
}

public void deleteRecursive(File file) {
    if (file.isDirectory()) {
        for (File child : file.listFiles()) {
            deleteRecursive(child);
        }
    }
    file.delete();
}

public void deleteRecursive(File basedir, String subdir) {
    try {
        deleteRecursive(new File(basedir, subdir));
    } catch (Exception ex) {
    }
}
